import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { mlService } from "./services/ml-service";
import { alertService } from "./services/alert-service";
import { websocketService } from "./services/websocket-service";
import { insertSiteSchema, insertSensorSchema, insertSensorReadingSchema, insertDroneImageSchema, insertAlertSchema, touristAlertSchema, userSettingsValidationSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket server for real-time alerts
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });
  websocketService.initialize(wss);

  // Prediction endpoint
  app.post("/api/v1/predict", async (req, res) => {
    try {
      const { siteId, timestamp, sensorData, imageUrl } = req.body;

      if (!siteId) {
        return res.status(400).json({ error: "Site ID is required" });
      }

      const prediction = await mlService.predict({
        siteId,
        timestamp: timestamp ? new Date(timestamp) : new Date(),
        sensorData,
        imageUrl
      });

      res.json(prediction);
    } catch (error) {
      console.error("Prediction error:", error);
      res.status(500).json({ error: "Prediction failed" });
    }
  });

  // Data ingestion endpoint
  app.post("/api/v1/ingest", async (req, res) => {
    try {
      const { type, data } = req.body;

      switch (type) {
        case "sensor_reading":
          const readingData = insertSensorReadingSchema.parse(data);
          const reading = await storage.createSensorReading(readingData);

          // Update sensor status
          await storage.updateSensorStatus(readingData.sensorId, "active");

          // Trigger prediction if needed
          const sensor = await storage.getSensor(readingData.sensorId);
          if (sensor) {
            mlService.queuePrediction(sensor.siteId);
          }

          res.json(reading);
          break;

        case "drone_image":
          const imageData = insertDroneImageSchema.parse(data);
          const image = await storage.createDroneImage(imageData);

          // Queue image processing
          mlService.queueImageProcessing(image.id);

          res.json(image);
          break;

        default:
          res.status(400).json({ error: "Invalid data type" });
      }
    } catch (error) {
      console.error("Ingestion error:", error);
      res.status(400).json({ error: "Invalid data format" });
    }
  });

  // Manual alert trigger
  app.post("/api/v1/alert", async (req, res) => {
    try {
      const alertData = insertAlertSchema.parse(req.body);
      const alert = await storage.createAlert(alertData);

      // Send notifications
      await alertService.sendAlert(alert);

      // Broadcast to WebSocket clients
      websocketService.broadcast({
        type: "alert",
        data: alert
      });

      res.json(alert);
    } catch (error) {
      console.error("Alert creation error:", error);
      res.status(400).json({ error: "Invalid alert data" });
    }
  });

  // Model registry
  app.get("/api/v1/models", async (req, res) => {
    try {
      const models = await storage.getModels();
      res.json(models);
    } catch (error) {
      console.error("Models fetch error:", error);
      res.status(500).json({ error: "Failed to fetch models" });
    }
  });

  // Set active model
  app.post("/api/v1/models/:id/activate", async (req, res) => {
    try {
      const { id } = req.params;
      const { type } = req.body;

      await storage.setActiveModel(id, type);
      res.json({ success: true });
    } catch (error) {
      console.error("Model activation error:", error);
      res.status(500).json({ error: "Failed to activate model" });
    }
  });

  // Sites management
  app.get("/api/v1/sites", async (req, res) => {
    try {
      const sites = await storage.getSites();
      res.json(sites);
    } catch (error) {
      console.error("Sites fetch error:", error);
      res.status(500).json({ error: "Failed to fetch sites" });
    }
  });

  app.get("/api/v1/sites/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const site = await storage.getSite(id);

      if (!site) {
        return res.status(404).json({ error: "Site not found" });
      }

      // Get additional site data
      const sensors = await storage.getSensorsBySite(id);
      const predictions = await storage.getPredictionsBySite(id, 10);
      const alerts = await storage.getAlertsBySite(id);
      const latestReadings = await storage.getLatestReadingsBySite(id);

      res.json({
        ...site,
        sensors,
        predictions,
        alerts,
        latestReadings
      });
    } catch (error) {
      console.error("Site fetch error:", error);
      res.status(500).json({ error: "Failed to fetch site" });
    }
  });

  app.post("/api/v1/sites", async (req, res) => {
    try {
      const siteData = insertSiteSchema.parse(req.body);
      const site = await storage.createSite(siteData);
      res.json(site);
    } catch (error) {
      console.error("Site creation error:", error);
      res.status(400).json({ error: "Invalid site data" });
    }
  });

  // Sensors management
  app.get("/api/v1/sites/:siteId/sensors", async (req, res) => {
    try {
      const { siteId } = req.params;
      const sensors = await storage.getSensorsBySite(siteId);
      res.json(sensors);
    } catch (error) {
      console.error("Sensors fetch error:", error);
      res.status(500).json({ error: "Failed to fetch sensors" });
    }
  });

  app.post("/api/v1/sites/:siteId/sensors", async (req, res) => {
    try {
      const { siteId } = req.params;
      const sensorData = insertSensorSchema.parse({ ...req.body, siteId });
      const sensor = await storage.createSensor(sensorData);
      res.json(sensor);
    } catch (error) {
      console.error("Sensor creation error:", error);
      res.status(400).json({ error: "Invalid sensor data" });
    }
  });

  // Sensor readings
  app.get("/api/v1/sensors/:sensorId/readings", async (req, res) => {
    try {
      const { sensorId } = req.params;
      const { from, to } = req.query;

      const fromDate = from ? new Date(from as string) : undefined;
      const toDate = to ? new Date(to as string) : undefined;

      const readings = await storage.getSensorReadings(sensorId, fromDate, toDate);
      res.json(readings);
    } catch (error) {
      console.error("Readings fetch error:", error);
      res.status(500).json({ error: "Failed to fetch readings" });
    }
  });

  // Alerts management
  app.get("/api/v1/alerts", async (req, res) => {
    try {
      const alerts = await storage.getActiveAlerts();
      res.json(alerts);
    } catch (error) {
      console.error("Alerts fetch error:", error);
      res.status(500).json({ error: "Failed to fetch alerts" });
    }
  });

  app.post("/api/v1/alerts/:id/acknowledge", async (req, res) => {
    try {
      const { id } = req.params;
      const { userId } = req.body;

      await storage.acknowledgeAlert(id, userId);

      // Broadcast acknowledgment
      websocketService.broadcast({
        type: "alert_acknowledged",
        data: { alertId: id, userId }
      });

      res.json({ success: true });
    } catch (error) {
      console.error("Alert acknowledgment error:", error);
      res.status(500).json({ error: "Failed to acknowledge alert" });
    }
  });

  // Dashboard metrics
  app.get("/api/v1/dashboard/metrics", async (req, res) => {
    try {
      const metrics = await storage.getRiskMetrics();

      // Get active model performance
      const activeModel = await storage.getActiveModel("fusion");
      const modelAccuracy = activeModel?.metrics ? 
        (activeModel.metrics as any).accuracy * 100 : 94.7;

      res.json({
        ...metrics,
        modelAccuracy: modelAccuracy.toFixed(1)
      });
    } catch (error) {
      console.error("Metrics fetch error:", error);
      res.status(500).json({ error: "Failed to fetch metrics" });
    }
  });

  // Dashboard trends
  app.get("/api/v1/dashboard/trends/:timeRange?", async (req, res) => {
    try {
      const { timeRange = "24h" } = req.params;

      // Calculate time window
      let timeWindow = 24; // hours
      switch (timeRange) {
        case "7d":
          timeWindow = 24 * 7;
          break;
        case "30d":
          timeWindow = 24 * 30;
          break;
        default:
          timeWindow = 24;
      }

      // Generate sample trend data for the time range
      const endTime = new Date();
      const startTime = new Date(endTime.getTime() - timeWindow * 60 * 60 * 1000);

      // Get recent predictions for trending
      const predictions = await storage.getPredictionsBySite("", 100); // Get from all sites

      // Generate trend data points
      const dataPoints: Array<{
        timestamp: string;
        averageRisk: number;
        activeSensors: number;
        alertCount: number;
      }> = [];

      const pointCount = Math.min(timeWindow / (timeRange === "24h" ? 1 : timeRange === "7d" ? 6 : 24), 50);

      for (let i = 0; i < pointCount; i++) {
        const pointTime = new Date(startTime.getTime() + (i * (timeWindow * 60 * 60 * 1000) / pointCount));

        dataPoints.push({
          timestamp: pointTime.toISOString(),
          averageRisk: 0.2 + Math.random() * 0.6, // Random between 0.2 and 0.8
          activeSensors: 20 + Math.floor(Math.random() * 10), // Random between 20-30
          alertCount: Math.floor(Math.random() * 5) // Random between 0-4
        });
      }

      res.json({
        timeRange,
        data: dataPoints,
        summary: {
          totalDataPoints: dataPoints.length,
          averageRisk: dataPoints.reduce((sum, p) => sum + p.averageRisk, 0) / dataPoints.length,
          peakRisk: Math.max(...dataPoints.map(p => p.averageRisk)),
          totalAlerts: dataPoints.reduce((sum, p) => sum + p.alertCount, 0)
        }
      });
    } catch (error) {
      console.error("Trends fetch error:", error);
      res.status(500).json({ error: "Failed to fetch trends" });
    }
  });

  // Predictions history
  app.get("/api/v1/predictions", async (req, res) => {
    try {
      const { siteId, limit } = req.query;

      if (!siteId) {
        return res.status(400).json({ error: "Site ID is required" });
      }

      const predictions = await storage.getPredictionsBySite(
        siteId as string, 
        limit ? parseInt(limit as string) : 10
      );

      res.json(predictions);
    } catch (error) {
      console.error("Predictions fetch error:", error);
      res.status(500).json({ error: "Failed to fetch predictions" });
    }
  });

  // Drone images
  app.get("/api/v1/sites/:siteId/images", async (req, res) => {
    try {
      const { siteId } = req.params;
      const images = await storage.getDroneImagesBySite(siteId);
      res.json(images);
    } catch (error) {
      console.error("Images fetch error:", error);
      res.status(500).json({ error: "Failed to fetch images" });
    }
  });

  // User settings endpoints for tourist safety preferences
  app.get("/api/v1/user/settings", async (req, res) => {
    try {
      const userId = req.headers['user-id'] as string || "default"; // In real app, get from auth
      const settings = await storage.getUserSettings(userId);

      if (!settings) {
        // Return default settings if none exist
        const defaultSettings = {
          geoFence: { enabled: true, radius: 5, alertOnExit: true, alertOnEntry: false },
          alertTypes: { medical: true, weather: true, crime: false, naturalDisaster: true, wildlife: true, accident: true, other: false },
          severityFilter: { critical: true, high: true, medium: false, low: false },
          notifications: { pushNotifications: true, emailAlerts: false, smsAlerts: false, soundEnabled: true, vibrationEnabled: true },
          contacts: { emergencyContact1: "", emergencyContact2: "", medicalInfo: "", bloodType: "", allergies: "" },
          location: { shareLocation: true, highAccuracyMode: false, locationHistory: true, batteryOptimization: true }
        };
        return res.json(defaultSettings);
      }

      // Convert database format to frontend format
      const formattedSettings = {
        geoFence: {
          enabled: settings.geoFenceEnabled,
          radius: settings.geoFenceRadius,
          alertOnExit: settings.alertOnExit,
          alertOnEntry: settings.alertOnEntry
        },
        alertTypes: settings.alertTypes as any,
        severityFilter: settings.severityFilter as any,
        notifications: settings.notificationSettings as any,
        contacts: {
          emergencyContact1: settings.emergencyContact1 || "",
          emergencyContact2: settings.emergencyContact2 || "",
          medicalInfo: settings.medicalInfo || "",
          bloodType: settings.bloodType || "",
          allergies: settings.allergies || ""
        },
        location: {
          shareLocation: settings.shareLocation,
          highAccuracyMode: settings.highAccuracyMode,
          locationHistory: settings.locationHistory,
          batteryOptimization: settings.batteryOptimization
        }
      };

      res.json(formattedSettings);
    } catch (error) {
      console.error("Error fetching user settings:", error);
      res.status(500).json({ error: "Failed to fetch user settings" });
    }
  });

  app.put("/api/v1/user/settings", async (req, res) => {
    try {
      // Validate settings structure
      const validationResult = userSettingsValidationSchema.safeParse(req.body);
      if (!validationResult.success) {
        return res.status(400).json({ 
          error: "Invalid settings format", 
          details: validationResult.error.issues 
        });
      }

      const userId = req.headers['user-id'] as string || "default"; // In real app, get from auth
      const settingsData = validationResult.data;

      const updatedSettings = await storage.updateUserSettings(userId, settingsData);

      res.json({ message: "Settings updated successfully", settings: updatedSettings });
    } catch (error) {
      console.error("Error updating user settings:", error);
      res.status(500).json({ error: "Failed to update user settings" });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { 
  sites, sensors, sensorReadings, droneImages, predictions, alerts, models, alertNotifications, users, userSettings,
  type Site, type InsertSite, type Sensor, type InsertSensor, type SensorReading, type InsertSensorReading,
  type DroneImage, type InsertDroneImage, type Prediction, type InsertPrediction, type Alert, type InsertAlert,
  type Model, type InsertModel, type AlertNotification, type InsertAlertNotification, type User, type InsertUser,
  type UserSettings, type InsertUserSettings
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, lte, sql } from "drizzle-orm";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Sites
  getSites(): Promise<Site[]>;
  getSite(id: string): Promise<Site | undefined>;
  createSite(site: InsertSite): Promise<Site>;
  updateSiteRiskLevel(id: string, riskLevel: string): Promise<void>;

  // Sensors
  getSensorsBySite(siteId: string): Promise<Sensor[]>;
  getSensor(id: string): Promise<Sensor | undefined>;
  createSensor(sensor: InsertSensor): Promise<Sensor>;
  updateSensorStatus(id: string, status: string, batteryLevel?: number): Promise<void>;

  // Sensor Readings
  getSensorReadings(sensorId: string, from?: Date, to?: Date): Promise<SensorReading[]>;
  createSensorReading(reading: InsertSensorReading): Promise<SensorReading>;
  getLatestReadingsBySite(siteId: string): Promise<(SensorReading & { sensor: Sensor })[]>;

  // Drone Images
  getDroneImagesBySite(siteId: string): Promise<DroneImage[]>;
  createDroneImage(image: InsertDroneImage): Promise<DroneImage>;
  markImageProcessed(id: string): Promise<void>;

  // Predictions
  getPredictionsBySite(siteId: string, limit?: number): Promise<Prediction[]>;
  getLatestPrediction(siteId: string): Promise<Prediction | undefined>;
  createPrediction(prediction: InsertPrediction): Promise<Prediction>;

  // Alerts
  getActiveAlerts(): Promise<(Alert & { site: Site })[]>;
  getAlertsBySite(siteId: string): Promise<Alert[]>;
  createAlert(alert: InsertAlert): Promise<Alert>;
  acknowledgeAlert(id: string, userId: string): Promise<void>;

  // Models
  getModels(): Promise<Model[]>;
  getActiveModel(type: string): Promise<Model | undefined>;
  createModel(model: InsertModel): Promise<Model>;
  setActiveModel(id: string, type: string): Promise<void>;

  // Alert Notifications
  createAlertNotification(notification: InsertAlertNotification): Promise<AlertNotification>;
  updateNotificationStatus(id: string, status: string): Promise<void>;

  // User Settings for Tourist Safety
  getUserSettings(userId?: string): Promise<UserSettings | undefined>;
  updateUserSettings(userId: string, settings: any): Promise<UserSettings>;

  // Analytics
  getRiskMetrics(): Promise<{
    highRisk: number;
    mediumRisk: number;
    lowRisk: number;
    activeSensors: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async getSites(): Promise<Site[]> {
    return await db.select().from(sites).orderBy(sites.name);
  }

  async getSite(id: string): Promise<Site | undefined> {
    const [site] = await db.select().from(sites).where(eq(sites.id, id));
    return site || undefined;
  }

  async createSite(insertSite: InsertSite): Promise<Site> {
    const [site] = await db.insert(sites).values(insertSite).returning();
    return site;
  }

  async updateSiteRiskLevel(id: string, riskLevel: string): Promise<void> {
    await db.update(sites)
      .set({ riskLevel, updatedAt: new Date() })
      .where(eq(sites.id, id));
  }

  async getSensorsBySite(siteId: string): Promise<Sensor[]> {
    return await db.select().from(sensors).where(eq(sensors.siteId, siteId));
  }

  async getSensor(id: string): Promise<Sensor | undefined> {
    const [sensor] = await db.select().from(sensors).where(eq(sensors.id, id));
    return sensor || undefined;
  }

  async createSensor(insertSensor: InsertSensor): Promise<Sensor> {
    const [sensor] = await db.insert(sensors).values(insertSensor).returning();
    return sensor;
  }

  async updateSensorStatus(id: string, status: string, batteryLevel?: number): Promise<void> {
    const updateData: any = { status, lastReading: new Date() };
    if (batteryLevel !== undefined) {
      updateData.batteryLevel = batteryLevel;
    }
    await db.update(sensors).set(updateData).where(eq(sensors.id, id));
  }

  async getSensorReadings(sensorId: string, from?: Date, to?: Date): Promise<SensorReading[]> {
    let conditions = [eq(sensorReadings.sensorId, sensorId)];

    if (from) {
      conditions.push(gte(sensorReadings.timestamp, from));
    }
    if (to) {
      conditions.push(lte(sensorReadings.timestamp, to));
    }

    return await db
      .select()
      .from(sensorReadings)
      .where(and(...conditions))
      .orderBy(desc(sensorReadings.timestamp));
  }

  async createSensorReading(insertReading: InsertSensorReading): Promise<SensorReading> {
    const [reading] = await db.insert(sensorReadings).values(insertReading).returning();
    return reading;
  }

  async getLatestReadingsBySite(siteId: string): Promise<(SensorReading & { sensor: Sensor })[]> {
    return await db
      .select({
        id: sensorReadings.id,
        sensorId: sensorReadings.sensorId,
        value: sensorReadings.value,
        unit: sensorReadings.unit,
        quality: sensorReadings.quality,
        timestamp: sensorReadings.timestamp,
        sensor: sensors
      })
      .from(sensorReadings)
      .innerJoin(sensors, eq(sensorReadings.sensorId, sensors.id))
      .where(eq(sensors.siteId, siteId))
      .orderBy(desc(sensorReadings.timestamp))
      .limit(50);
  }

  async getDroneImagesBySite(siteId: string): Promise<DroneImage[]> {
    return await db.select().from(droneImages)
      .where(eq(droneImages.siteId, siteId))
      .orderBy(desc(droneImages.captureTime));
  }

  async createDroneImage(insertImage: InsertDroneImage): Promise<DroneImage> {
    const [image] = await db.insert(droneImages).values(insertImage).returning();
    return image;
  }

  async markImageProcessed(id: string): Promise<void> {
    await db.update(droneImages).set({ processed: true }).where(eq(droneImages.id, id));
  }

  async getPredictionsBySite(siteId: string, limit = 10): Promise<Prediction[]> {
    return await db.select().from(predictions)
      .where(eq(predictions.siteId, siteId))
      .orderBy(desc(predictions.timestamp))
      .limit(limit);
  }

  async getLatestPrediction(siteId: string): Promise<Prediction | undefined> {
    const [prediction] = await db.select().from(predictions)
      .where(eq(predictions.siteId, siteId))
      .orderBy(desc(predictions.timestamp))
      .limit(1);
    return prediction || undefined;
  }

  async createPrediction(insertPrediction: InsertPrediction): Promise<Prediction> {
    const [prediction] = await db.insert(predictions).values(insertPrediction).returning();
    return prediction;
  }

  async getActiveAlerts(): Promise<(Alert & { site: Site })[]> {
    return await db
      .select({
        id: alerts.id,
        siteId: alerts.siteId,
        predictionId: alerts.predictionId,
        type: alerts.type,
        severity: alerts.severity,
        title: alerts.title,
        message: alerts.message,
        actionPlan: alerts.actionPlan,
        status: alerts.status,
        acknowledgedBy: alerts.acknowledgedBy,
        acknowledgedAt: alerts.acknowledgedAt,
        createdAt: alerts.createdAt,
        site: sites
      })
      .from(alerts)
      .innerJoin(sites, eq(alerts.siteId, sites.id))
      .where(eq(alerts.status, "active"))
      .orderBy(desc(alerts.createdAt));
  }

  async getAlertsBySite(siteId: string): Promise<Alert[]> {
    return await db.select().from(alerts)
      .where(eq(alerts.siteId, siteId))
      .orderBy(desc(alerts.createdAt));
  }

  async createAlert(insertAlert: InsertAlert): Promise<Alert> {
    const [alert] = await db.insert(alerts).values(insertAlert).returning();
    return alert;
  }

  async acknowledgeAlert(id: string, userId: string): Promise<void> {
    await db.update(alerts)
      .set({ 
        status: "acknowledged", 
        acknowledgedBy: userId, 
        acknowledgedAt: new Date() 
      })
      .where(eq(alerts.id, id));
  }

  async getModels(): Promise<Model[]> {
    return await db.select().from(models).orderBy(desc(models.trainedAt));
  }

  async getActiveModel(type: string): Promise<Model | undefined> {
    const [model] = await db.select().from(models)
      .where(and(eq(models.type, type), eq(models.isActive, true)));
    return model || undefined;
  }

  async createModel(insertModel: InsertModel): Promise<Model> {
    const [model] = await db.insert(models).values(insertModel).returning();
    return model;
  }

  async setActiveModel(id: string, type: string): Promise<void> {
    // Deactivate all models of this type
    await db.update(models)
      .set({ isActive: false })
      .where(eq(models.type, type));

    // Activate the selected model
    await db.update(models)
      .set({ isActive: true })
      .where(eq(models.id, id));
  }

  async createAlertNotification(insertNotification: InsertAlertNotification): Promise<AlertNotification> {
    const [notification] = await db.insert(alertNotifications).values(insertNotification).returning();
    return notification;
  }

  async updateNotificationStatus(id: string, status: string): Promise<void> {
    const updateData: any = { status };
    if (status === "sent") {
      updateData.sentAt = new Date();
    }
    await db.update(alertNotifications).set(updateData).where(eq(alertNotifications.id, id));
  }

  async getUserSettings(userId?: string): Promise<UserSettings | undefined> {
    const targetUserId = userId || "default";
    const [settings] = await db.select().from(userSettings).where(eq(userSettings.userId, targetUserId));
    return settings || undefined;
  }

  async updateUserSettings(userId: string, settingsData: any): Promise<UserSettings> {
    const targetUserId = userId || "default";

    // Check if settings exist
    const existing = await this.getUserSettings(targetUserId);

    if (existing) {
      // Update existing settings
      const [updated] = await db.update(userSettings).set({
        geoFenceEnabled: settingsData.geoFence.enabled,
        geoFenceRadius: settingsData.geoFence.radius,
        alertOnExit: settingsData.geoFence.alertOnExit,
        alertOnEntry: settingsData.geoFence.alertOnEntry,
        alertTypes: settingsData.alertTypes,
        severityFilter: settingsData.severityFilter,
        notificationSettings: settingsData.notifications,
        emergencyContact1: settingsData.contacts.emergencyContact1,
        emergencyContact2: settingsData.contacts.emergencyContact2,
        medicalInfo: settingsData.contacts.medicalInfo,
        bloodType: settingsData.contacts.bloodType,
        allergies: settingsData.contacts.allergies,
        shareLocation: settingsData.location.shareLocation,
        highAccuracyMode: settingsData.location.highAccuracyMode,
        locationHistory: settingsData.location.locationHistory,
        batteryOptimization: settingsData.location.batteryOptimization,
        updatedAt: new Date(),
      }).where(eq(userSettings.userId, targetUserId)).returning();
      return updated;
    } else {
      // Create new settings
      const [created] = await db.insert(userSettings).values({
        userId: targetUserId,
        geoFenceEnabled: settingsData.geoFence.enabled,
        geoFenceRadius: settingsData.geoFence.radius,
        alertOnExit: settingsData.geoFence.alertOnExit,
        alertOnEntry: settingsData.geoFence.alertOnEntry,
        alertTypes: settingsData.alertTypes,
        severityFilter: settingsData.severityFilter,
        notificationSettings: settingsData.notifications,
        emergencyContact1: settingsData.contacts.emergencyContact1,
        emergencyContact2: settingsData.contacts.emergencyContact2,
        medicalInfo: settingsData.contacts.medicalInfo,
        bloodType: settingsData.contacts.bloodType,
        allergies: settingsData.contacts.allergies,
        shareLocation: settingsData.location.shareLocation,
        highAccuracyMode: settingsData.location.highAccuracyMode,
        locationHistory: settingsData.location.locationHistory,
        batteryOptimization: settingsData.location.batteryOptimization,
      }).returning();
      return created;
    }
  }

  async getRiskMetrics(): Promise<{
    highRisk: number;
    mediumRisk: number;
    lowRisk: number;
    activeSensors: number;
  }> {
    const [riskCounts] = await db
      .select({
        highRisk: sql<number>`count(*) filter (where risk_level in ('high', 'critical'))`,
        mediumRisk: sql<number>`count(*) filter (where risk_level = 'medium')`,
        lowRisk: sql<number>`count(*) filter (where risk_level = 'low')`,
      })
      .from(sites)
      .where(eq(sites.isActive, true));

    const [sensorCount] = await db
      .select({
        activeSensors: sql<number>`count(*)`
      })
      .from(sensors)
      .where(eq(sensors.status, "active"));

    return {
      highRisk: riskCounts.highRisk,
      mediumRisk: riskCounts.mediumRisk,
      lowRisk: riskCounts.lowRisk,
      activeSensors: sensorCount.activeSensors,
    };
  }
}

export const storage = new DatabaseStorage();

