            <p className="text-xs text-muted-foreground">Safety Coordinator</p>
          </div>
          <Button variant="ghost" size="sm" data-testid="button-user-settings">
            <Settings className="h-4 w-4" />
          </Button>
        </div>
      </div>
    </aside>
  );
}

