import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Bell, CheckCircle, AlertTriangle, Info, X, Filter } from "lucide-react";
import Header from "@/components/layout/header";
import { formatDistanceToNow } from "date-fns";

/**
 * Notifications Page Component
 * Displays all alerts and notifications from the system
 * Allows filtering by type, severity, and status
 * Connected to the bell icon in the navigation header
 */
export default function Notifications() {
  const [filter, setFilter] = useState("all");

  // Fetch alerts from backend
  const { data: alerts = [], isLoading } = useQuery<any[]>({
    queryKey: ["/api/v1/alerts"],
    refetchInterval: 5000, // Refresh every 5 seconds for real-time updates
  });

  // Filter alerts based on selected filter
  const filteredAlerts = alerts.filter((alert: any) => {
    switch (filter) {
      case "active":
        return alert.status === "active";
      case "acknowledged":
        return alert.status === "acknowledged";
      case "critical":
        return alert.severity === "critical";
      case "emergency":
        return alert.type === "emergency";
      default:
        return true;
    }
  });

  // Group alerts by status for better organization
  const activeAlerts = alerts.filter((alert: any) => alert.status === "active");
  const acknowledgedAlerts = alerts.filter((alert: any) => alert.status === "acknowledged");

  return (
    <>
      <Header 
        title="Notifications & Alerts" 
        subtitle={`${activeAlerts.length} active alerts, ${acknowledgedAlerts.length} acknowledged`}
      />

      <div className="p-6">
        {/* Filter Controls */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-2">
            <Filter className="w-4 h-4 text-muted-foreground" />
            <span className="text-sm text-muted-foreground">Filter:</span>
            <div className="flex space-x-2">
              {["all", "active", "critical", "emergency", "acknowledged"].map((filterOption) => (
                <Button
                  key={filterOption}
                  variant={filter === filterOption ? "default" : "outline"}
                  size="sm"
                  onClick={() => setFilter(filterOption)}
                  data-testid={`filter-${filterOption}`}
                >
                  {filterOption.charAt(0).toUpperCase() + filterOption.slice(1)}
                </Button>
              ))}
            </div>
          </div>

          <div className="text-sm text-muted-foreground">
            Last updated: {new Date().toLocaleTimeString()}
          </div>
        </div>

        <Tabs defaultValue="all" className="space-y-6">
          <TabsList>
            <TabsTrigger value="all">All Notifications</TabsTrigger>
            <TabsTrigger value="active">
              Active Alerts
              {activeAlerts.length > 0 && (
                <Badge variant="destructive" className="ml-2">
                  {activeAlerts.length}
                </Badge>
              )}
            </TabsTrigger>
            <TabsTrigger value="acknowledged">Acknowledged</TabsTrigger>
          </TabsList>

          <TabsContent value="all" className="space-y-4">
            <NotificationList 
              alerts={filteredAlerts} 
              isLoading={isLoading}
              showAll={true}
            />
          </TabsContent>

          <TabsContent value="active" className="space-y-4">
            <NotificationList 
              alerts={activeAlerts} 
              isLoading={isLoading}
              showAll={false}
            />
          </TabsContent>

          <TabsContent value="acknowledged" className="space-y-4">
            <NotificationList 
              alerts={acknowledgedAlerts} 
              isLoading={isLoading}
              showAll={false}
            />
          </TabsContent>
        </Tabs>
      </div>
    </>
  );
}

/**
 * NotificationList Component
 * Renders a list of notification cards with alert details
 */
interface NotificationListProps {
  alerts: any[];
  isLoading: boolean;
  showAll: boolean;
}

function NotificationList({ alerts, isLoading, showAll }: NotificationListProps) {
  if (isLoading) {
    return (
      <div className="space-y-4">
        {[1, 2, 3].map((i) => (
          <Card key={i} className="animate-pulse">
            <CardHeader className="pb-3">
              <div className="h-4 bg-muted rounded w-3/4"></div>
              <div className="h-3 bg-muted rounded w-1/2"></div>
            </CardHeader>
            <CardContent>
              <div className="h-3 bg-muted rounded w-full mb-2"></div>
              <div className="h-3 bg-muted rounded w-2/3"></div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (alerts.length === 0) {
    return (
      <Card className="text-center py-12">
        <CardContent>
          <Bell className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
          <CardTitle className="text-muted-foreground mb-2">No notifications</CardTitle>
          <CardDescription>
            {showAll ? "All caught up! No new notifications." : "No notifications in this category."}
          </CardDescription>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {alerts.map((alert: any) => (
        <NotificationCard key={alert.id} alert={alert} />
      ))}
    </div>
  );
}

/**
 * Individual Notification Card Component
 * Displays alert details with appropriate styling based on severity
 */
interface NotificationCardProps {
  alert: any;
}

function NotificationCard({ alert }: NotificationCardProps) {
  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case "critical":
        return "border-red-500 bg-red-50 dark:bg-red-950/20";
      case "high":
        return "border-orange-500 bg-orange-50 dark:bg-orange-950/20";
      case "medium":
        return "border-yellow-500 bg-yellow-50 dark:bg-yellow-950/20";
      default:
        return "border-blue-500 bg-blue-50 dark:bg-blue-950/20";
    }
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case "critical":
        return <AlertTriangle className="w-5 h-5 text-red-600" />;
      case "high":
        return <AlertTriangle className="w-5 h-5 text-orange-600" />;
      case "medium":
        return <Info className="w-5 h-5 text-yellow-600" />;
      default:
        return <Info className="w-5 h-5 text-blue-600" />;
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "active":
        return <Badge variant="destructive">Active</Badge>;
      case "acknowledged":
        return <Badge variant="secondary">Acknowledged</Badge>;
      case "resolved":
        return <Badge variant="outline">Resolved</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  return (
    <Card 
      className={`${getSeverityColor(alert.severity)} transition-all hover:shadow-md`}
      data-testid={`alert-card-${alert.id}`}
    >
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-start space-x-3">
            {getSeverityIcon(alert.severity)}
            <div className="flex-1">
              <CardTitle className="text-base font-medium">
                {alert.title}
              </CardTitle>
              <CardDescription className="mt-1">
                {alert.site?.name && `Site: ${alert.site.name} â€¢ `}
                {formatDistanceToNow(new Date(alert.createdAt), { addSuffix: true })}
              </CardDescription>
            </div>
          </div>
          <div className="flex items-center space-x-2">
            {getStatusBadge(alert.status)}
            <Badge variant="outline" className="text-xs">
              {alert.severity.toUpperCase()}
            </Badge>
          </div>
        </div>
      </CardHeader>

      <CardContent className="pt-0">
        <div className="space-y-3">
          {/* Alert Message */}
          <p className="text-sm text-foreground">
            {alert.message}
          </p>

          {/* Action Plan - if available */}
          {alert.actionPlan && (
            <div className="bg-background/50 rounded-lg p-3">
              <h4 className="text-xs font-medium text-muted-foreground mb-1">
                RECOMMENDED ACTIONS:
              </h4>
              <pre className="text-xs text-foreground whitespace-pre-wrap font-sans">
                {alert.actionPlan}
              </pre>
            </div>
          )}

          {/* Acknowledgment Info */}
          {alert.status === "acknowledged" && alert.acknowledgedBy && (
            <div className="flex items-center space-x-2 text-xs text-muted-foreground">
              <CheckCircle className="w-3 h-3" />
              <span>
                Acknowledged by {alert.acknowledgedBy}
                {alert.acknowledgedAt && ` on ${new Date(alert.acknowledgedAt).toLocaleString()}`}
              </span>
            </div>
          )}

          {/* Action Buttons */}
          {alert.status === "active" && (
            <div className="flex space-x-2 pt-2 border-t">
              <Button 
                size="sm" 
                variant="outline"
                data-testid={`acknowledge-alert-${alert.id}`}
                onClick={() => {
                  // TODO: Implement acknowledge functionality
                  console.log("Acknowledge alert:", alert.id);
                }}
              >
                <CheckCircle className="w-3 h-3 mr-1" />
                Acknowledge
              </Button>
              <Button 
                size="sm" 
                variant="ghost"
                data-testid={`dismiss-alert-${alert.id}`}
                onClick={() => {
                  // TODO: Implement dismiss functionality
                  console.log("Dismiss alert:", alert.id);
                }}
              >
                <X className="w-3 h-3 mr-1" />
                Dismiss
              </Button>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/pages/reports.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { CalendarIcon, Download, Search, Filter, MapPin, Clock, AlertTriangle } from "lucide-react";
import { format, parseISO, startOfDay, endOfDay } from "date-fns";
import Header from "@/components/layout/header";

/**
 * Reports Page Component
 * Displays historical alerts and reports with search/filter functionality
 * Includes data export capabilities and detailed incident analysis
 */
export default function Reports() {
  const [searchTerm, setSearchTerm] = useState("");
  const [typeFilter, setTypeFilter] = useState("all");
  const [severityFilter, setSeverityFilter] = useState("all");
  const [statusFilter, setStatusFilter] = useState("all");
  const [dateRange, setDateRange] = useState<{from?: Date; to?: Date}>({});

  // Fetch historical alerts and reports
  const { data: reports = [], isLoading, error } = useQuery<any[]>({
    queryKey: ["/api/v1/alerts", searchTerm, typeFilter, severityFilter, statusFilter, dateRange],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Apply filters to reports
  const filteredReports = reports.filter((report: any) => {
    // Search filter
    if (searchTerm && !report.title.toLowerCase().includes(searchTerm.toLowerCase()) && 
        !report.message.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }

    // Type filter
    if (typeFilter !== "all" && report.type !== typeFilter) {
      return false;
    }

    // Severity filter
    if (severityFilter !== "all" && report.severity !== severityFilter) {
      return false;
    }

    // Status filter
    if (statusFilter !== "all" && report.status !== statusFilter) {
      return false;
    }

    // Date range filter
    if (dateRange.from && dateRange.to) {
      const reportDate = parseISO(report.createdAt);
      if (reportDate < startOfDay(dateRange.from) || reportDate > endOfDay(dateRange.to)) {
        return false;
      }
    }

    return true;
  });

  // Export reports to CSV
  const handleExportReports = () => {
    const csvContent = generateCSV(filteredReports);
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `tourist-safety-reports-${format(new Date(), 'yyyy-MM-dd')}.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    window.URL.revokeObjectURL(url);
  };

  return (
    <>
      <Header 
        title="Safety Reports" 
        subtitle={`${filteredReports.length} incidents found across all locations`}
      />

      <div className="p-6">
        {/* Filter Controls */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Filter className="w-5 h-5" />
              Filter & Search Reports
            </CardTitle>
            <CardDescription>
              Find specific incidents or analyze patterns in tourist safety data
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              {/* Search */}
              <div className="space-y-2">
                <Label>Search</Label>
                <div className="relative">
                  <Search className="absolute left-2 top-2.5 h-4 w-4 text-muted-foreground" />
                  <Input
                    placeholder="Search incidents..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-8"
                    data-testid="input-search"
                  />
                </div>
              </div>

              {/* Type Filter */}
              <div className="space-y-2">
                <Label>Alert Type</Label>
                <Select value={typeFilter} onValueChange={setTypeFilter}>
                  <SelectTrigger data-testid="select-type-filter">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Types</SelectItem>
                    <SelectItem value="emergency">Emergency</SelectItem>
                    <SelectItem value="medical">Medical</SelectItem>
                    <SelectItem value="accident">Accident</SelectItem>
                    <SelectItem value="weather">Weather</SelectItem>
                    <SelectItem value="crime">Crime/Security</SelectItem>
                    <SelectItem value="natural_disaster">Natural Disaster</SelectItem>
                    <SelectItem value="wildlife">Wildlife</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Severity Filter */}
              <div className="space-y-2">
                <Label>Severity</Label>
                <Select value={severityFilter} onValueChange={setSeverityFilter}>
                  <SelectTrigger data-testid="select-severity-filter">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Severities</SelectItem>
                    <SelectItem value="critical">Critical</SelectItem>
                    <SelectItem value="high">High</SelectItem>
                    <SelectItem value="medium">Medium</SelectItem>
                    <SelectItem value="low">Low</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Status Filter */}
              <div className="space-y-2">
                <Label>Status</Label>
                <Select value={statusFilter} onValueChange={setStatusFilter}>
                  <SelectTrigger data-testid="select-status-filter">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Statuses</SelectItem>
                    <SelectItem value="active">Active</SelectItem>
                    <SelectItem value="acknowledged">Acknowledged</SelectItem>
                    <SelectItem value="resolved">Resolved</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            {/* Date Range Picker */}
            <div className="mt-4 flex items-center gap-4">
              <div className="space-y-2">
                <Label>Date Range</Label>
                <div className="flex gap-2">
                  <Popover>
                    <PopoverTrigger asChild>
                      <Button variant="outline" className="w-[240px] justify-start text-left font-normal">
                        <CalendarIcon className="mr-2 h-4 w-4" />
                        {dateRange.from ? (
                          dateRange.to ? (
                            <>
                              {format(dateRange.from, "LLL dd, y")} -{" "}
                              {format(dateRange.to, "LLL dd, y")}
                            </>
                          ) : (
                            format(dateRange.from, "LLL dd, y")
                          )
                        ) : (
                          <span>Pick a date range</span>
                        )}
                      </Button>
                    </PopoverTrigger>
                    <PopoverContent className="w-auto p-0" align="start">
                      <Calendar
                        initialFocus
                        mode="range"
                        defaultMonth={dateRange.from}
                        selected={dateRange as any}
                        onSelect={(range) => setDateRange(range || {})}
                        numberOfMonths={2}
                      />
                    </PopoverContent>
                  </Popover>

                  {(dateRange.from || dateRange.to) && (
                    <Button
                      variant="outline"
                      onClick={() => setDateRange({})}
                    >
                      Clear
                    </Button>
                  )}
                </div>
              </div>

              <div className="ml-auto">
                <Button 
                  onClick={handleExportReports}
                  variant="outline"
                  className="flex items-center gap-2"
                  data-testid="button-export-reports"
                >
                  <Download className="w-4 h-4" />
                  Export CSV
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Reports Table */}
        <Card>
          <CardHeader>
            <CardTitle>Incident Reports</CardTitle>
            <CardDescription>
              Comprehensive history of all safety alerts and emergency incidents
            </CardDescription>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="flex items-center justify-center py-8">
                <div className="text-center">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
                  <p className="text-muted-foreground">Loading reports...</p>
                </div>
              </div>
            ) : error ? (
              <div className="text-center py-8">
                <AlertTriangle className="w-12 h-12 text-destructive mx-auto mb-4" />
                <p className="text-destructive">Failed to load reports</p>
              </div>
            ) : filteredReports.length === 0 ? (
              <div className="text-center py-8">
                <Search className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-muted-foreground">No reports found matching your criteria</p>
              </div>
            ) : (
              <div className="overflow-x-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Date & Time</TableHead>
                      <TableHead>Incident Type</TableHead>
                      <TableHead>Severity</TableHead>
                      <TableHead>Location</TableHead>
                      <TableHead>Description</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {filteredReports.map((report: any) => (
                      <TableRow key={report.id} data-testid={`report-row-${report.id}`}>
                        <TableCell>
                          <div className="flex items-center gap-2">
                            <Clock className="w-4 h-4 text-muted-foreground" />
                            <div>
                              <div className="font-medium">
                                {format(parseISO(report.createdAt), "MMM dd, yyyy")}
                              </div>
                              <div className="text-sm text-muted-foreground">
                                {format(parseISO(report.createdAt), "HH:mm")}
                              </div>
                            </div>
                          </div>
                        </TableCell>

                        <TableCell>
                          <Badge variant="outline" className="capitalize">
                            {report.type.replace('_', ' ')}
                          </Badge>
                        </TableCell>

                        <TableCell>
                          <Badge 
                            variant={
                              report.severity === "critical" ? "destructive" :
                              report.severity === "high" ? "default" :
                              "secondary"
                            }
                          >
                            {report.severity.toUpperCase()}
                          </Badge>
                        </TableCell>

                        <TableCell>
                          <div className="flex items-center gap-2 max-w-[200px]">
                            <MapPin className="w-4 h-4 text-muted-foreground flex-shrink-0" />
                            <span className="truncate">
                              {report.site?.name || "Unknown Location"}
                            </span>
                          </div>
                        </TableCell>

                        <TableCell>
                          <div className="max-w-[300px]">
                            <p className="font-medium truncate">{report.title}</p>
                            <p className="text-sm text-muted-foreground truncate">
                              {report.message}
                            </p>
                          </div>
                        </TableCell>

                        <TableCell>
                          <Badge 
                            variant={
                              report.status === "active" ? "destructive" :
                              report.status === "acknowledged" ? "default" :
                              "outline"
                            }
                          >
                            {report.status}
                          </Badge>
                        </TableCell>

                        <TableCell>
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={() => {
                              // TODO: Implement view details functionality
                              console.log("View report details:", report.id);
                            }}
                            data-testid={`button-view-details-${report.id}`}
                          >
                            View Details
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Summary Statistics */}
        {filteredReports.length > 0 && (
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mt-6">
            <Card>
              <CardContent className="p-4">
                <div className="text-2xl font-bold text-destructive">
                  {filteredReports.filter(r => r.severity === "critical").length}
                </div>
                <p className="text-sm text-muted-foreground">Critical Incidents</p>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-4">
                <div className="text-2xl font-bold text-orange-600">
                  {filteredReports.filter(r => r.severity === "high").length}
                </div>
                <p className="text-sm text-muted-foreground">High Priority</p>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-4">
                <div className="text-2xl font-bold text-green-600">
                  {filteredReports.filter(r => r.status === "resolved").length}
                </div>
                <p className="text-sm text-muted-foreground">Resolved</p>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-4">
                <div className="text-2xl font-bold">
                  {Math.round((filteredReports.filter(r => r.status === "resolved").length / filteredReports.length) * 100)}%
                </div>
                <p className="text-sm text-muted-foreground">Resolution Rate</p>
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </>
  );
}

/**
 * Generate CSV content from reports data
 */
function generateCSV(reports: any[]): string {
  const headers = [
    "Date",
    "Time", 
    "Type",
    "Severity",
    "Title",
    "Description",
    "Location",
    "Status",
    "Acknowledged By",
    "Acknowledged At"
  ];

  const rows = reports.map(report => [
    format(parseISO(report.createdAt), "yyyy-MM-dd"),
    format(parseISO(report.createdAt), "HH:mm:ss"),
    report.type,
    report.severity,
    report.title,
    report.message.replace(/"/g, '""'), // Escape quotes for CSV
    report.site?.name || "",
    report.status,
    report.acknowledgedBy || "",
    report.acknowledgedAt ? format(parseISO(report.acknowledgedAt), "yyyy-MM-dd HH:mm:ss") : ""
  ]);

  const csvContent = [headers, ...rows]
    .map(row => row.map(field => `"${field}"`).join(","))
    .join("\n");

  return csvContent;
}
